import React from 'react';
import { Button, FormGroup, FormControl, ControlLabel } from 'reactstrap';

export default class Login extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            username: "",
            password: ""
        };
    }

    validateForm() {
        return this.state.username.length > 0 && this.state.password.length > 0;
    }

    handleChange = event => {
        this.setState({
            [event.target.id]: event.target.value
        });
    }

    handleSubmit = event => {
        event.preventDefault();
        console.log(event);
    }

    render() {
        return (
            <div className="Login container-fluid">
                <form onSubmit={this.handleSubmit}>
                    <FormGroup controlId="username" bsSize="large">
                        <ControlLabel>Username: </ControlLabel>
                        <FormControl autoFocus type="email" value={this.state.username} onChange={this.handleChange}/>
                    </FormGroup>
                    <FormGroup controlId="password" bsSize="large">
                        <ControlLabel>Password: </ControlLabel>
                        <FormControl type="password" value={this.state.username} onChange={this.handleChange}/>
                    </FormGroup>
                    <Button block bsSize="large" disabled={!this.validateForm()} type="submit"> Login </Button>
                </form>
            </div>
        );
    }
}
